# --- –õ–æ–≥–∏–∫–∞ —Ä–∞—Å—á—ë—Ç–æ–≤ ---
def reduce_to_single(num):
    """–†–µ–¥—É—Ü–∏—Ä–æ–≤–∞–Ω–∏–µ —á–∏—Å–ª–∞ –¥–æ –æ–¥–Ω–æ–∑–Ω–∞—á–Ω–æ–≥–æ (1-9)."""
    while num > 9:
        num = sum(int(d) for d in str(num))
    return num if num != 0 else 9


def calculate_numbers(day, month, year):
    jiwa = reduce_to_single(day)
    month_s = reduce_to_single(month)
    dharma = reduce_to_single(jiwa + month_s)
    year_s = reduce_to_single(year)
    karma = reduce_to_single(dharma + year_s)
    # 2026 ‚Üí 2+0+2+6 = 10 ‚Üí 1
    current_year_num = reduce_to_single(2 + 0 + 2 + 6)
    forecast = reduce_to_single(jiwa + current_year_num)
    return jiwa, dharma, karma, forecast


def parse_one_date(s):
    """–ü–∞—Ä—Å–∏—Ç —Å—Ç—Ä–æ–∫—É –≤ (–¥–µ–Ω—å, –º–µ—Å—è—Ü, –≥–æ–¥) –∏–ª–∏ –≤–æ–∑–≤—Ä–∞—â–∞–µ—Ç None."""
    clean = re.sub(r'[^0-9]', ' ', s).strip()
    parts = re.split(r'\s+', clean)

    if len(parts) == 3:
        try:
            d, m, y = int(parts[0]), int(parts[1]), int(parts[2])
            if y < 100:
                y += 1900 if y > 30 else 2000
            if 1 <= d <= 31 and 1 <= m <= 12 and 1900 <= y <= 2100:
                return d, m, y
        except (ValueError, IndexError):
            pass

    # –ü–æ–ø—ã—Ç–∫–∞ –¥–ª—è —Å–ª–∏—Ç–Ω—ã—Ö —Ñ–æ—Ä–º–∞—Ç–æ–≤ ddmmyyyy –∏–ª–∏ ddmmyy
    digits = re.sub(r'\s', '', clean)
    if len(digits) == 8:
        try:
            d, m, y = int(digits[:2]), int(digits[2:4]), int(digits[4:])
            if 1 <= d <= 31 and 1 <= m <= 12 and 1900 <= y <= 2100:
                return d, m, y
        except ValueError:
            pass
    if len(digits) == 6:
        try:
            d, m, y = int(digits[:2]), int(digits[2:4]), int(digits[4:])
            y += 1900 if y > 30 else 2000
            if 1 <= d <= 31 and 1 <= m <= 12 and 1900 <= y <= 2100:
                return d, m, y
        except ValueError:
            pass
    return None


def parse_date(date_str):
    """
    –í–æ–∑–≤—Ä–∞—â–∞–µ—Ç:
    - tuple (d, m, y) –¥–ª—è –æ–¥–Ω–æ–π –¥–∞—Ç—ã
    - list [str_date1, str_date2] –¥–ª—è –¥–≤—É—Ö –¥–∞—Ç
    - None –µ—Å–ª–∏ –Ω–µ —É–¥–∞–ª–æ—Å—å —Ä–∞—Å–ø–æ–∑–Ω–∞—Ç—å
    """
    s = date_str.strip()
    # –ü–æ–ø—ã—Ç–∫–∞ —Ä–∞–∑–¥–µ–ª–∏—Ç—å –Ω–∞ 2 –¥–∞—Ç—ã (—Ä–∞–∑–¥–µ–ª–∏—Ç–µ–ª—å ‚Äî –ø—Ä–æ–±–µ–ª –∏–ª–∏ –ø–µ—Ä–µ–Ω–æ—Å —Å—Ç—Ä–æ–∫–∏)
    # –ò—â–µ–º —à–∞–±–ª–æ–Ω: –¥–≤–µ –≥—Ä—É–ø–ø—ã —Ü–∏—Ñ—Ä —Å —Ä–∞–∑–¥–µ–ª–∏—Ç–µ–ª—è–º–∏, —Ä–∞–∑–¥–µ–ª—ë–Ω–Ω—ã–µ –ø—Ä–æ–±–µ–ª–æ–º
    two_date_pattern = re.compile(
        r'^(\d{1,2}[.\-/]\d{1,2}[.\-/]\d{2,4})\s+(\d{1,2}[.\-/]\d{1,2}[.\-/]\d{2,4})$'
    )
    m = two_date_pattern.match(s)
    if m:
        d1 = parse_one_date(m.group(1))
        d2 = parse_one_date(m.group(2))
        if d1 and d2:
            return [m.group(1), m.group(2)]

    # –û–¥–Ω–∞ –¥–∞—Ç–∞
    return parse_one_date(s)


def get_compatibility_text(j1, j2):
    """–í–æ–∑–≤—Ä–∞—â–∞–µ—Ç —Ç–µ–∫—Å—Ç —Å–æ–≤–º–µ—Å—Ç–∏–º–æ—Å—Ç–∏ –ø–æ –ø–∞—Ä–µ –¥–∂–∏–≤–∞-—á–∏—Å–µ–ª."""
    lo, hi = min(j1, j2), max(j1, j2)
    key = f"{lo}_{hi}"
    return CONTENT_DB["compatibility"].get(key)


